import React, {useState} from 'react'
import ExpenseItem from './ExpenseItem'
import './Expenses.css'
import Card from '../UI/Card'
import ExpensesFilter from './ExpensesFilter'
function Expenses(props) {
  const [filteredYear,setFilteredYear]=useState(2019)
  const filterHandler = (year)=>{
          setFilteredYear(year);
  }
  const filteredExpenses = props.expenses.filter(expense => {
    return expense.date.getFullYear().toString()==filteredYear;
  })
  let expensesContent = <p>No data found.</p>
  if(filteredExpenses.length>0){
    expensesContent = filteredExpenses.map(expense => <ExpenseItem key = {expense.id} title = {expense.title} amount = {expense.amount} date = {expense.date}/>)
  }
  return (
    <div>
    <Card className='expenses'>
    <ExpensesFilter selected = {filteredYear} changeHandler = {filterHandler}/>
    {expensesContent}

    </Card>
    </div>
  )
}

export default Expenses
